mapper.update:
update ls_task
set description = #{description},
    desc_imgs   = #{descImgs},
    deadline    = #{deadline},
    update_time = #{updateTime}
where id = #{id}

update ls_task
set available = '0'
where id = #{id}

mapper.select:
select * from ls_task


        and description like CONCAT ('%',#{description},'%')


        and duty_id = #{dutyId}


        and user_name like CONCAT ('%',#{userName},'%')


        and publish_time  >=  #{publishTimeStart}


        and publish_time  <=  #{publishTimeEnd}

    and available = '1'

order by create_time desc
